import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import instance from '../axiosConfig';

export const fetchTasks = createAsyncThunk('tasks/fetchTasks', async () => {
  const response = await instance.get('/tasks');
  return response.data;
});

export const createTask = createAsyncThunk('tasks/createTask', async (task) => {
  const response = await instance.post('/tasks', task);
  return response.data;
});

export const toggleTask = createAsyncThunk('tasks/toggleTask', async (id) => {
  const response = await instance.put(`/tasks/${id}/toggle`);
  return response.data;
});

export const deleteTask = createAsyncThunk('tasks/deleteTask', async (id) => {
  await instance.delete(`/tasks/${id}`);
  return id;
});

const taskSlice = createSlice({
  name: 'tasks',
  initialState: [],
  extraReducers: (builder) => {
    builder
      .addCase(fetchTasks.fulfilled, (state, action) => action.payload)
      .addCase(createTask.fulfilled, (state, action) => {
        state.push(action.payload);
      })
      .addCase(toggleTask.fulfilled, (state, action) => {
        const index = state.findIndex((task) => task.id === action.payload.id);
        if (index !== -1) state[index] = action.payload;
      })
      .addCase(deleteTask.fulfilled, (state, action) =>
        state.filter((task) => task.id !== action.payload)
      );
  },
});

export default taskSlice.reducer;
