import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { createTask, updateTask } from "../redux/taskSlice";

const TaskForm = ({ selectedTask, setSelectedTask }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const dispatch = useDispatch();

  useEffect(() => {
    if (selectedTask) {
      setTitle(selectedTask.title);
      setDescription(selectedTask.description || "");
    }
  }, [selectedTask]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedTask) {
      dispatch(updateTask({ id: selectedTask.id, updatedTask: { title, description } }));
      setSelectedTask(null);
    } else {
      dispatch(createTask({ title, description }));
    }
    setTitle("");
    setDescription("");
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      <h2>{selectedTask ? "Edit Task" : "Add New Task"}</h2>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Task Title"
        required
      />
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Task Description"
      />
      <button type="submit">{selectedTask ? "Update Task" : "Add Task"}</button>
      {selectedTask && (
        <button type="button" onClick={() => setSelectedTask(null)}>
          Cancel
        </button>
      )}
    </form>
  );
};

export default TaskForm;
