import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchTasks } from './redux/taskSlice';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import TaskFilters from './components/TaskFilters';

const App = () => {
  const dispatch = useDispatch();
  const { tasks, isLoading, error } = useSelector((state) => state.tasks);

  useEffect(() => {
    dispatch(fetchTasks());
  }, [dispatch]);

  return (
    <div className="min-h-screen bg-background flex flex-col items-center p-6">
      <header className="w-full max-w-4xl text-center mb-8">
        <h1 className="text-4xl font-bold text-primary">Todo App</h1>
        <p className="text-gray-500 mt-2">Manage your tasks efficiently</p>
      </header>
      <main className="w-full max-w-4xl">
        <TaskForm />
        <TaskFilters />
        {isLoading ? (
          <p className="text-center text-secondary">Loading tasks...</p>
        ) : error ? (
          <p className="text-center text-red-500">Error: {error}</p>
        ) : (
          <TaskList tasks={tasks} />
        )}
      </main>
      <footer className="w-full text-center mt-8 text-gray-400">
        <p>Â© {new Date().getFullYear()} Todo App</p>
      </footer>
    </div>
  );
};

export default App;
