import React from 'react';
import { useDispatch } from 'react-redux';
import { toggleTask, deleteTask } from '../redux/taskSlice';

const TaskList = ({ tasks }) => {
  const dispatch = useDispatch();

  if (!tasks.length) {
    return <p className="text-gray-500">No tasks available.</p>;
  }

  return (
    <ul className="w-full max-w-md bg-white shadow-lg rounded p-4">
      {tasks.map((task) => (
        <li
          key={task.id}
          className={`flex justify-between items-center border-b py-2 ${
            task.completed ? 'bg-green-100' : ''
          }`}
        >
          <div>
            <h3
              className={`cursor-pointer text-lg ${
                task.completed ? 'line-through text-green-600' : ''
              }`}
              onClick={() => dispatch(toggleTask(task.id))}
            >
              {task.title}
            </h3>
            <p className="text-sm text-gray-600">{task.description}</p>
          </div>
          <button
            onClick={() => dispatch(deleteTask(task.id))}
            className="text-red-500 hover:text-red-700"
          >
            Delete
          </button>
        </li>
      ))}
    </ul>
  );
};

export default TaskList;
