import React from "react";
import { useDispatch } from "react-redux";
import { deleteTask } from "../redux/taskSlice";

const TaskList = ({ tasks, setSelectedTask }) => {
  const dispatch = useDispatch();

  if (!tasks.length) {
    return <p className="text-gray-500">No tasks available.</p>;
  }

  return (
    <ul className="w-full max-w-md divide-y divide-gray-200">
      {tasks.map((task) => (
        <li key={task.id} className="flex justify-between items-center py-2">
          <span className={`cursor-pointer ${task.completed ? "line-through text-gray-500" : ""}`}>
            {task.title}
          </span>
          <div className="space-x-2">
            <button
              onClick={() => setSelectedTask(task)}
              className="text-blue-500 hover:text-blue-700 transition"
            >
              Edit
            </button>
            <button
              onClick={() => dispatch(deleteTask(task.id))}
              className="text-red-500 hover:text-red-700 transition"
            >
              Delete
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default TaskList;
