import { createSlice, configureStore, createAsyncThunk } from '@reduxjs/toolkit';
import axios from '../axiosConfig';

// Thunk para obtener las tareas desde el backend
export const fetchTasks = createAsyncThunk('tasks/fetchTasks', async () => {
  const response = await axios.get('/tasks');
  return response.data;
});

// Thunk para crear una nueva tarea
export const createTask = createAsyncThunk('tasks/createTask', async (task) => {
  const response = await axios.post('/tasks', task);
  return response.data;
});

// Slice para manejar el estado de las tareas
const tasksSlice = createSlice({
  name: 'tasks',
  initialState: [],
  reducers: {
    toggleTask(state, action) {
      const task = state.find((t) => t.id === action.payload);
      if (task) task.completed = !task.completed;
    },
    deleteTask(state, action) {
      return state.filter((t) => t.id !== action.payload);
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchTasks.fulfilled, (state, action) => action.payload)
      .addCase(createTask.fulfilled, (state, action) => {
        state.push(action.payload);
      });
  },
});

export const { toggleTask, deleteTask } = tasksSlice.actions;

export default configureStore({
  reducer: {
    tasks: tasksSlice.reducer,
  },
});
